mapper.select:
select qna_num,mem_id,qna_title,qna_content,qna_ref,qna_re_step,qna_re_level,qna_views,qna_date,qna_img,qna_authority,qna_ask
from (
	select a.*,rownum rnum
	from(
		select * from qna


				qna_title like '%' || #{keyWord} || '%'


				qna_content like '%' || #{keyWord} || '%'


				mem_id like '%' || #{keyWord} || '%'


		order by qna_num desc, qna_ref desc, qna_re_step asc
	) a
)

	where rnum >= #{start} and rnum <= #{end}
